```js
// File: /controllers/file.js
// Example:

exports.install = function() {

	ROUTE('POST /upload/', upload, ['upload'], 1024 * 5); // Max. 5 MB

};

function upload() {

	var self = this;
	var output = [];
	var items = [];

	// We store all uploaded fiels
	self.files.wait(function(file, next) {

		var obj = {};

		obj.id = UID();
		obj.filename = file.filename;
		obj.size = file.size;
		obj.type = file.type;
		obj.ext = file.extension;
		obj.url = '/downloads/' + obj.id + '.' + obj.ext;

		file.fs('files', obj.id, function(err) {

			if (!err)
				output.push(obj);

			next();
		});

	}, function() {

		// Returns JSON with list of uploaded and stored files
		self.json(output);

	});
}
```

